import React from "react";
import emailjs from "emailjs";
import { useRef } from "react";

import Navigation from "../navigation/navigation"

import {
    ContactContainer, 
    ContactContent,
    Title,
    ContactForm,
    Label,
    Button
} from "./styles"

const InputStyle = {
    padding: "10px",
    lineHeight: 1,
    marginLeft: "5rem",
    fontSize: "16px",
    width: "20rem",
    color: "#292826",
    borderRadius: "10px"
}

const DescriptionStyle = {
    padding: "10px",
    lineHeight: 1,
    marginLeft: "2.5rem",
    fontSize: "16px",
    width: "20rem",
    color: "#292826",
    borderRadius: "10px"
}

const Contact = () => {
    const form = useRef();

    const sendEmail = (event) => {
        event.preventDefault();

        emailjs
        .sendForm(
            "service_b4qmiqc",
            "template_h9rzd14",
            form.current,
            "user_UHpNJFij8MtQD1aAfs38X"
        )
        .then(
            (result) => {
                console.log(result.text);
                alert("SUCCESS!");
            },
            (error) => {
                console.log(error.text);
                alert("FAILED...", error);
            }
        );
    };

    return(
        <ContactContainer>
             <Title>Let's Talk</Title>

            <form ref={form} onSubmit={sendEmail}>
                <div className="form-group">
                    <label for="name">Name</label>
                    <input
                        type="name"
                        name="name"
                        className="form-control"
                        id="name"
                        placeholder="enter your name"
                    />
                </div>

                        {/* <!-- email --> */}
                        <div className="form-group">
                            <label for="email">Email address</label>
                            <input
                                type="email"
                                name="email"
                                className="form-control"
                                id="email"
                                placeholder="enter your email"
                            />
                        </div>

                        {/* <!-- subject --> */}
                        <div className="form-group">
                            <label for="subject">Subject</label>
                            <input
                                type="text"
                                name="subject"
                                className="form-control"
                                id="subject"
                                placeholder="enter email subject"
                            />
                        </div>

                        <div className="form-group">
                            <label for="email_body">Message</label>
                            <textarea
                                name="message"
                                className="form-control"
                                id="email_body"
                                rows="5"
                            ></textarea>
                        </div>

                        <button type="submit" className="btn btn-primary">
                            Submit
                        </button>
                    </form>
        </ContactContainer>
    );
}

export default Contact;
