    {
        filterId: 2,
        label: "FrontEnd",
    },
    {
        filterId: 3,
        label: "BackEnd",
    },
];

// Projects Data
const projectsData = [
    {
        projectId: 1,
        name: "Halo Clothing",
        image: "",
        link: "",
    },
    {
        projectId: 1,
        name: "Todo App",
        link: "",
        image: "",
    },
    {
        projectId: 2,
        name: "African Marketplace",
        image: "",
        link: "",
    },
    {
        projectId: 3,
        name: "Potluck Planner",
        image: "",
        link: "",
    },
    {
        projectId: 2,
        name: "CoderHeroes",
        image: "",
        link: "",
    },
];

const Projects = () => {
    const [filterState, setfilterState] = useState(1);
    const [hoverState, sethoverState] = useState(null);

    return(
        <div className="portfolio-content">
            <ul className="portfolio-content-filter">
                {filterData.map((item) => (
                    <li
                    className={item.filterId === filteredvalue ? "active" : ""}
                    onClick={() => handleFilter(item.filterId)}
                    key={item.filterId}
                    >
                    {item.label}
                    </li>
                ))}
            </ul>

            <div className="portfolio-content-cards">
                {filteredItems.map((item, index) => (
                    <div
                        className="portfolio-content-cards-item"
                        key={`cardItem${item.name.trim()}`}
                        onMouseEnter={() => handleHover(index)}
                        onMouseLeave={() => handleHover(null)}
                    >
                    <div className="portfolio-content-cards-item-img-wrapper">
                        <a>
                        <img alt="dummy data" src={item.image} />
                        </a>
                    </div>
                    <div className="overlay">
                        {index === hoveredValue && (
                        <div>
                            <p>{item.name}</p>
                            <button>Visit</button>
                        </div>
                        )}
                    </div>
                    </div>
                ))}
            </div>
      </div>
    );
}

export default Projects;
