import React from "react";

import Navigation from "../navigation/navigation"

import {
    ContactContainer, 
    ContactContent,
    Title,
    ContactForm,
    Label,
    Button
} from "./styles"

const InputStyle = {
    padding: "10px",
    lineHeight: 1,
    marginLeft: "5rem",
    fontSize: "16px",
    width: "20rem",
    color: "#292826",
    borderRadius: "10px"
}

const DescriptionStyle = {
    padding: "10px",
    lineHeight: 1,
    marginLeft: "2.5rem",
    fontSize: "16px",
    width: "20rem",
    color: "#292826",
    borderRadius: "10px"
}

const Contact = () => {
    const form = useRef();

    const sendEmail = (e) => {
        e.preventDefault();

        emailjs
        .sendForm(
            "service_b4qmiqc",
            "template_h9rzd14",
            form.current,
            "user_UHpNJFij8MtQD1aAfs38X"
        )
        .then(
            (result) => {
                console.log(result.text);
                alert("SUCCESS!");
            },
            (error) => {
            console.log(error.text);
            alert("FAILED...", error);
            }
        );
    };

    return(
        <ContactContainer>
            <Navigation/>
            <ContactContent>
                <Title>Let's Chat!</Title>

                <div className="contact-form-container">
                    <ContactForm>
                        <div>
                            <Label>
                                Name

                                <input
                                    required
                                    name="name"
                                    className="inputName"
                                    type={"text"}
                                    style={InputStyle}
                                />
                            </Label>
                        </div>

                        <div>
                            <Label>
                                Email

                                <input
                                    required
                                    name="email"
                                    className="inputEmail"
                                    type={"text"}
                                    style={InputStyle}
                                />
                            </Label>
                        </div>

                        <div>
                            <Label>
                                Description

                                <textarea
                                    required
                                    name="description"
                                    className="inputDescription"
                                    type={"text"}
                                    rows="5"
                                    style={DescriptionStyle}
                                />
                            </Label>
                        </div>
                    </ContactForm>

                    <Button>Submit</Button>
                </div>
            </ContactContent>
        </ContactContainer>
    );
}

export default Contact;
